// WAP to print 10 ,8 ,6 ,4 ,2 using For loop
public class EvenNum10to1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("Even num 10 to 1 using for loop");
		
		for(int i=10;i>=1;i--)
		{
			if(i%2==0)
			{
				System.out.println(i);
			}
		}
		
	}

}
