// WAP to print 10 ,8 ,6 ,4 ,2 using While loop
public class Even_num10to1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i=10;
		System.out.println("Even num 10 to 1 using while loop");
		while(i>=1)
		{
			if(i%2==0)
			{
				System.out.println(i);
			}
			i--;
		}
	}

}
