//WAP to print Triangle pattern printing 

package Assignment_java;

public class Pattern_Print_Triangle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i,j;
		for(i=1;i<6;i++)
		{
			for(j=1;j<=i;j++)
			{
				System.out.print(" *");
			}
			System.out.println("");
		}
	}

}
